  /**
   * 获取HH:mm时刻的地支序号，非法的时刻返回0
   * @param hm HH:mm时刻
   * @return 地支序号，0到11
   */
  public static int getTimeZhiIndex(String hm){
    if(null==hm){
      return 0;
    }
    if(hm.length()>5){
      hm = hm.substring(0,5);
    }
    int x = 1;
    for(int i=1;i<22;i+=2){
      if(hm.compareTo((i<10?"0":"")+i+":00")>=0&&hm.compareTo((i+1<10?"0":"")+(i+1)+":59")<=0){
        return x;
      }
      x++;
    }
    return 0;
  }

  /**
   * 将HH:mm时刻转换为时辰（地支），非法的时刻返回子
   * @param hm HH:mm时刻
   * @return 时辰(地支)，如子
   */
  public static String convertTime(String hm){
    return ZHI[getTimeZhiIndex(hm)+1];
  }

  /**
   * 数字转十六进制
   * @param n 数字
   * @return 十六进制
   */
  private static String hex(int n){
    String hex = Integer.toHexString(n&0xFF);
    if(hex.length()<2){
      hex = "0"+hex;
    }
    return hex.toUpperCase();
  }

  /**
   * 获取干支对应的甲子序号
   * @param ganZhi 干支
   * @return 甲子序号
   */
  public static int getJiaZiIndex(String ganZhi){
    return find(ganZhi, JIA_ZI, 0);
  }

  public static int find(String name, String[] names, int offset) {
    for(int i=0,j=names.length;i<j;i++){
      if(names[i].equals(name)){
        return i + offset;
      }
    }
    return -1;
  }

  private static List<String> getDayYiJi(String monthGanZhi, String dayGanZhi, String pattern) {
    List<String> l = new ArrayList<String>();
    String day = hex(getJiaZiIndex(dayGanZhi));
    String month = hex(getJiaZiIndex(monthGanZhi));
    Matcher matcher = Pattern.compile(day + "=(.[^;]+?);").matcher(DAY_YI_JI);
    while (matcher.find()) {
      String v = matcher.group(1);
      if (Pattern.compile(month + "(?:\\w{2})*:").matcher(v).find()) {
        matcher = Pattern.compile(pattern).matcher(v);
        if (matcher.find()) {
          String s = matcher.group(1);
          for (int i = 0, j = s.length(); i < j; i += 2) {
            l.add(YI_JI[Integer.parseInt(s.substring(i, i + 2), 16)]);
          }
        }
        break;
      }
    }
    if (l.isEmpty()) {
      l.add("无");
    }
    return l;
  }

  /**
   * 获取日宜
   * @param monthGanZhi 月干支
   * @param dayGanZhi 日干支
   * @return 宜
   */
  public static List<String> getDayYi(String monthGanZhi, String dayGanZhi){
    return getDayYiJi(monthGanZhi, dayGanZhi, ":(.*),");
  }

  /**
   * 获取日忌
   * @param monthGanZhi 月干支
   * @param dayGanZhi 日干支
   * @return 忌
   */
  public static List<String> getDayJi(String monthGanZhi, String dayGanZhi){
    return getDayYiJi(monthGanZhi, dayGanZhi, ",(.*)");
  }

  /**
   * 获取日吉神
   * @param monthZhiIndex 月地支序号
   * @param dayGanZhi 日干支
   * @return 日吉神
   */
  public static List<String> getDayJiShen(int monthZhiIndex, String dayGanZhi){
    List<String> l = new ArrayList<String>();
    int m = monthZhiIndex - 2;
    if (m < 0) {
      m += 12;
    }
    Matcher matcher = Pattern.compile(String.format(";%02X(.[^;]*)", getJiaZiIndex(dayGanZhi))).matcher(DAY_SHEN_SHA[m]);
    if (matcher.find()) {
      String data = matcher.group(1);
      for (int i = 0, j = data.length(); i < j; i += 2) {
        int index = Integer.parseInt(data.substring(i, i + 2), 16);
        if (index < 60) {
          l.add(SHEN_SHA[index]);
        }
      }
    }
    if(l.isEmpty()){
      l.add("无");
    }
    return l;
  }

  /**
   * 获取日凶煞
   * @param monthZhiIndex 月地支序号
   * @param dayGanZhi 日干支
   * @return 日凶煞
   */
  public static List<String> getDayXiongSha(int monthZhiIndex, String dayGanZhi){
    List<String> l = new ArrayList<String>();
    int m = monthZhiIndex - 2;
    if (m < 0) {
      m += 12;
    }
    Matcher matcher = Pattern.compile(String.format(";%02X(.[^;]*)", getJiaZiIndex(dayGanZhi))).matcher(DAY_SHEN_SHA[m]);
    if (matcher.find()) {
      String data = matcher.group(1);
      for (int i = 0, j = data.length(); i < j; i += 2) {
        int index = Integer.parseInt(data.substring(i, i + 2), 16);
        if (index >= 60) {
          l.add(SHEN_SHA[index]);
        }
      }
    }
    if(l.isEmpty()){
      l.add("无");
    }
    return l;
  }

  /**
   * 获取时辰宜
   * @param dayGanZhi 日干支
   * @param timeGanZhi 时干支
   * @return 宜
   */
  public static List<String> getTimeYi(String dayGanZhi, String timeGanZhi){
    List<String> l = new ArrayList<String>();
    String day = hex(getJiaZiIndex(dayGanZhi));
    String time = hex(getJiaZiIndex(timeGanZhi));
    int index = TIME_YI_JI.indexOf(day+time+"=");
    if(index>-1) {
      String left = TIME_YI_JI.substring(index + 5);
      if (left.contains("=")) {
        left = left.substring(0, left.indexOf("=") - 4);
      }
      String ys = left.substring(0, left.indexOf(","));
      for (int i = 0, j = ys.length(); i < j; i += 2) {
        String m = ys.substring(i, i + 2);
        l.add(YI_JI[Integer.parseInt(m, 16)]);
      }
    }
    if(l.isEmpty()){
      l.add("无");
    }
    return l;
  }

  /**
   * 获取时辰忌
   * @param dayGanZhi 日干支
   * @param timeGanZhi 时干支
   * @return 忌
   */
  public static List<String> getTimeJi(String dayGanZhi, String timeGanZhi){
    List<String> l = new ArrayList<String>();
    String day = hex(getJiaZiIndex(dayGanZhi));
    String time = hex(getJiaZiIndex(timeGanZhi));
    int index = TIME_YI_JI.indexOf(day+time+"=");
    if(index>-1) {
      String left = TIME_YI_JI.substring(index + 5);
      if (left.contains("=")) {
        left = left.substring(0, left.indexOf("=") - 4);
      }
      String js = left.substring(left.indexOf(",")+1);
      for (int i = 0, j = js.length(); i < j; i += 2) {
        String m = js.substring(i, i + 2);
        l.add(YI_JI[Integer.parseInt(m, 16)]);
      }
    }
    if(l.isEmpty()){
      l.add("无");
    }
    return l;
  }

  /**
   * 获取干支所在旬下标，0-5
   * @param ganZhi 干支
   * @return 旬下标，0-5
   */
  protected static int getXunIndex(String ganZhi){
    int ganIndex = find(ganZhi.substring(0, 1), GAN, 0);
    int zhiIndex = find(ganZhi.substring(1), ZHI, 0);
    int diff = ganIndex - zhiIndex;
    if(diff<0){
      diff += 12;
    }
    return diff/2;
  }

  /**
   * 获取干支所在旬
   * @param ganZhi 干支
   * @return 旬
   */
  public static String getXun(String ganZhi){
    return XUN[getXunIndex(ganZhi)];
  }

